mapper.select:
select
a.*
from scheduleAgent a join agent_type b on a.type=b.id


        and b.can_create_events=#{canCreateEvents}


        and b.can_receive_events=#{canReceiveEvents}

SELECT * FROM scheduleAgent


        and name like #{name}


        and id in(
        SELECT agent_id FROM scenario_agent_rel WHERE scenario_id=#{scenarioId}
        )


order by created_at desc
